  def processInput(input: String): Seq[Either[Int, Int]] = {
    input.zipWithIndex.map { case (x, i) =>
      x match {
        case '(' => Right(1)
        case ')' => Right(-1)
        case _ => Left(i)
      }
    }
  }

  def Step1() {

    val result = processInput(input).flatMap(x => x.right.toOption).sum

    println(s"day 1_1 $result")
  }

  def Step2() {

    val result = processInput(input).flatMap(x => x.right.toOption)

    @tailrec
    def finder(ints: Seq[Int], accum: (Option[Int], Int)): (Option[Int], Int) = {
      if (ints.isEmpty) {
        (None, accum._2)
      } else if (accum._2 == -1) {
        accum
      } else {
        finder(ints.tail, (Some(accum._1.get + 1), accum._2 + ints.head))
      }
    }

    val index = finder(result, (Some(0), 0))

    println(s"day 1_2 $index")
  }

  def main(args: Array[String]): Unit = {
    Step1()
    Step2()
  }
}